template_content.module_manager:
&НаСервере
Функция НайтиПоКоду(Код, ИспользоватьПоискПоКоду = Истина) Экспорт
    // Поиск элемента справочника по коду

    Запрос = Новый Запрос;
    Запрос.Текст = "
        |ВЫБРАТЬ ПЕРВЫЕ 1
        |    Ссылка
        |ИЗ
        |    Справочник.Номенклатура КАК Таблица
        |ГДЕ
        |    Таблица.Код = &Код
        |";

    Запрос.УстановитьПараметр("Код", Код);

    Результат = Запрос.Выполнить();
    Выборка = Результат.Выбрать();

    Если Выборка.Следующий() Тогда
        Возврат Выборка.Ссылка;
    Иначе
        Возврат ПустаяСсылка();
    КонецЕсли;
КонецФункции

&НаСервере
Функция ПолучитьСписокПоУсловию(Условие) Экспорт
    // Получение списка элементов по условию

    Запрос = Новый Запрос;
    Запрос.Текст = "
        |ВЫБРАТЬ
        |    Ссылка,
        |    Код,
        |    Наименование
        |ИЗ
        |    Справочник.Номенклатура КАК Таблица
        |ГДЕ
        |    " + Условие + "
        |УПОРЯДОЧИТЬ ПО
        |    Наименование
        |";

    Результат = Запрос.Выполнить();
    Возврат Результат.Выгрузить();
КонецФункции
